$(function(){$('[data-toggle="popover"]').popover({html:!0,title:function(){return $(this).next().find(".popover-head").html()},content:function(){return $(this).next().find(".popover-content").html()}}),$('[id^="item"]').on("shown.bs.popover",function(){var o=parseInt($(this).offset().top)-70;$(".popover.left .arrow").css("top",o+"px")}),$('[data-toggle="popover"]').on("click",function(o){$('[data-toggle="popover"]').not(this).popover("hide")})});